# -*- coding: utf-8 -*-from odoo import models, api, fieldsclass AccountMove(models.Model):    _inherit = 'account.move'    post_date = fields.Date(string="Post Date")    description = fields.Char(string="Description")    def action_post(self):        res = super(AccountMove, self).action_post()        self.write({'post_date': fields.Date.today()})        return resclass AccountMoveLine(models.Model):    _inherit = 'account.move.line'    description = fields.Char(string="Description")