# -*- coding: utf-8 -*-from odoo import models, fields, api, Commandfrom dateutil.relativedelta import relativedeltafrom collections import defaultdictclass ApprovalCategory(models.Model):    _inherit = 'approval.category'    is_employee_manager_approval = fields.Boolean("Employee Manager's Approval")    is_it_device = fields.Boolean("Is IT Device")    @api.depends('approver_ids', 'is_employee_manager_approval')    def _compute_user_ids(self):        res = super(ApprovalCategory, self)._compute_user_ids()        for record in self:            manager_users = record.approver_ids.user_id.employee_id.parent_id.user_id            record.user_ids += manager_users        return res