# -*- coding: utf-8 -*-from odoo import models, fields, api, _from odoo.exceptions import ValidationErrorclass CasualLeaveReport(models.AbstractModel):    _name = 'report.ebs_jbm_hr_holiday_custom.casual_leave_template'    @api.model    def _get_report_values(self, docids, data=None):        leaves = self.env['hr.leave'].browse(docids)        if leaves and not all(leaves.holiday_status_id.mapped('is_casual_leave_type')):            raise ValidationError(_('Can\'t print this report for this type of leave ..........'))        docs = {            'docs': leaves,            'payment': leaves,        }        return docs