# -*- coding: utf-8 -*-from odoo import api, fields, models, _class CrossoveredBudgetLines(models.Model):    _inherit = "crossovered.budget.lines"    remaining_amount = fields.Monetary(        compute='_compute_remaining_amount', string='Remaining Amount')    @api.depends('planned_amount', 'practical_amount', 'amount_of_reservation')    def _compute_remaining_amount(self):        for rec in self:            rec.remaining_amount = rec.planned_amount - (rec.practical_amount + rec.amount_of_reservation)