# -*- coding: utf-8 -*-from odoo import models, fields, apifrom odoo.http import requestclass HRAttendance(models.AbstractModel):    _inherit = 'hr.attendance'    arabic_name = fields.Char(string="Arabic Name", compute="_compute_arabic_name", store=True)    visible_name = fields.Char(string="Employee", compute="_get_employee_name")    def _get_employee_name(self):        for record in self:            if record.env.user.lang == "ar_001":                record.visible_name = record.arabic_name            else:                record.visible_name = record.employee_id.name    @api.depends('employee_id', 'employee_id.arabic_name')    def _compute_arabic_name(self):        for rec in self:            rec.arabic_name = rec.employee_id.arabic_name if rec.employee_id and rec.employee_id.arabic_name else False