# -*- coding: utf-8 -*-from odoo import models, fields, apifrom odoo.http import requestclass HREmployeePublic(models.AbstractModel):    _inherit = 'hr.employee.base'    office_floor = fields.Char(string="Office Floor")    office_no = fields.Char(string="Office NO.")class HREmployee(models.Model):    _inherit = 'hr.employee'    contract_id = fields.Many2one(groups=False)    country_id = fields.Many2one(groups=False)    @api.model    def get_hr_services_records(self):        uid = request.session.uid        login_user = self.env['res.users'].sudo().search([('id', '=', uid)], limit=1)        related_employee = self.env['hr.employee'].sudo().search([('user_id', '=', uid)], limit=1)        if related_employee:            if not login_user.has_group('jbm_portal_self_service.group_self_service_manager'):                domain = [('employee_id', '=', login_user.employee_id.id)]            else:                domain = ['|', ('employee_id', '=', login_user.employee_id.id),                          ('employee_id', 'child_of', login_user.employee_id.id)]            leaves_count = self.env['hr.leave'].search(domain)            return {'leaves_count': leaves_count}        else:            return False