from odoo import models, fields, api, _from odoo.exceptions import ValidationErrorclass RFQTerms(models.Model):    _name = "rfq.terms"    _description = "RFQ terms and conditions"    rfq_terms_condition_ar = fields.Html(string="Terms And Condition (arabic)")    rfq_terms_condition_en = fields.Html(string="Terms And Condition (English)")    note_en = fields.Html(string="Note (English)")    note_ar = fields.Html(string="Note (Arabic)")    is_used = fields.Boolean(string="Used", default=True)    @api.constrains('is_used')    def check_is_used_duplication(self):        if len(self.search([('is_used', '=', True)])) > 1:            if self.env.context.get('lang') == 'ar_001':                raise ValidationError(_("لا يمكنك استخدام اكثر من شروط واحكام لعرض السعر"))            else:                raise ValidationError(_("You can\'t use more than one terms and conditions record for RFQ"))